class testpoint(unittest.TestCase):
	
	def test_weather_response(self):
		self.maxDiff = None
		self.assertEqual(weather_response("California","ef1e66b70e237d45d7f99d9134a5c802"), ('b\'%s\'' %json1))
		self.assertEqual(weather_response("123","04c43aacf7d595bcc104140cbe737838"), ('b\'%s\'' %json2))
		self.assertEqual(weather_response("Del","2ab136be1543b5789451a5994364c0d3"), ('b\'%s\'' %json3))
		
	def test_has_error(self):
		self.assertFalse(has_error("California", json1))
		self.assertFalse(has_error("123", json2))
		self.assertFalse(has_error("Del", json3))
		self.assertTrue(has_error(" ", json2))
		self.assertTrue(has_error("#%!", json3))

	def test_get_temperature(self):
		self.assertAlmostEqual(get_temperature(json1, 2, "15:00:00"), 287.691 ,delta=5)
		self.assertAlmostEqual(get_temperature(json2, 3, "06:00:00"), 301.826 ,delta=5)
		self.assertAlmostEqual(get_temperature(json3, 4, "21:00:00"), 284.103 ,delta=5)
	
	def test_get_humidity(self):
		self.assertAlmostEqual(get_humidity(json1, 2, "15:00:00"), 100 ,delta=5)
		self.assertAlmostEqual(get_humidity(json2, 3, "06:00:00"), 94 ,delta=5)
		self.assertAlmostEqual(get_humidity(json3, 4, "21:00:00"), 56 ,delta=5)

	def test_get_pressure(self):
		self.assertAlmostEqual(get_pressure(json1, 2, "15:00:00"), 818.53 ,delta=5)
		self.assertAlmostEqual(get_pressure(json2, 3, "06:00:00"), 1008.56 ,delta=5)
		self.assertAlmostEqual(get_pressure(json3, 4, "21:00:00"), 880.93 ,delta=5)
	
	def test_get_wind(self):
		self.assertAlmostEqual(get_wind(json1, 2, "15:00:00"), 1.8 ,delta=5)
		self.assertAlmostEqual(get_wind(json2, 3, "06:00:00"), 4.81 ,delta=5)
		self.assertAlmostEqual(get_wind(json3, 4, "21:00:00"), 1.96 ,delta=5)

	def test_get_sealevel(self):
		self.assertAlmostEqual(get_sealevel(json1, 2, "15:00:00"), 1025.6 ,delta=5)
		self.assertAlmostEqual(get_sealevel(json2, 3, "06:00:00"), 1022.8 ,delta=5)
		self.assertAlmostEqual(get_sealevel(json3, 4, "21:00:00"), 1026.08 ,delta=5)
	
